export interface Element {
  number: number;
  symbol: string;
  name: string;
  nameFr: string;
  mass: number;
  category: string;
  group: number | null;
  period: number;
  block: string;
  electronConfig: string;
  electronegativity: number | null;
  meltingPoint: number | null; // Kelvin
  boilingPoint: number | null; // Kelvin
  density: number | null; // g/cm³
  abundanceUniverse: number | null; // ppm by mass
  abundanceEarth: number | null; // ppm by mass
  origin: string; // nucleosynthesis origin
  discoveredBy: string;
  yearDiscovered: number | null;
  description: string;
  uses: string[];
  color: string; // category color key
}

export type ElementCategory =
  | 'alkali-metal'
  | 'alkaline-earth-metal'
  | 'transition-metal'
  | 'post-transition-metal'
  | 'metalloid'
  | 'reactive-nonmetal'
  | 'noble-gas'
  | 'lanthanide'
  | 'actinide'
  | 'unknown';

