export const allElements: Element[] = [
  ...elements,
  ...fillElements(),
].sort((a, b) => a.number - b.number);

// Add placeholder elements for the complete periodic table
export const getElement = (numberOrSymbol: number | string): Element | undefined => {
  if (typeof numberOrSymbol === 'number') {
    return allElements.find(e => e.number === numberOrSymbol);
  }
  return allElements.find(e => e.symbol === numberOrSymbol || e.name.toLowerCase() === (numberOrSymbol as string).toLowerCase() || e.nameFr.toLowerCase() === (numberOrSymbol as string).toLowerCase());
};

// Get top elements by universe abundance
export const topUniverseAbundance = [...allElements]
  .filter(e => e.abundanceUniverse !== null && e.abundanceUniverse! > 0)
  .sort((a, b) => (b.abundanceUniverse ?? 0) - (a.abundanceUniverse ?? 0))
  .slice(0, 15);

// Get top elements by earth crust abundance
export const topEarthAbundance = [...allElements]
  .filter(e => e.abundanceEarth !== null && e.abundanceEarth! > 0)
  .sort((a, b) => (b.abundanceEarth ?? 0) - (a.abundanceEarth ?? 0))
  .slice(0, 15);

